import './Login.css';
import { useRef } from 'react';

function Login() {

  const passwordEle = useRef(null)
  function togglePasswordVisibility(){
    if(passwordEle.current.type == 'text'){
      passwordEle.current.type = 'password'
    }else{
      passwordEle.current.type = 'text'
    }
  }

  return (
    <section className="vh-100 gradient-custom">
        <div className="container py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-8 col-lg-6 col-xl-5">
              <div className="card bg-transparent text-white" style={{borderRadius: "1rem", border: "3px solid #fff"}}>
                <div className="card-body p-4 py-5">

                  <div className="mb-md-3 pb-3">

                    <h2 className="fw-bold mb-4 text-uppercase text-center">Login</h2>

                    <form action="{% url 'userLogin' %}" method="post" className="form">

                        <div className="form-outline mb-3">
                          <input type="text" id="typeUsernameX" name="username" className="form-control form-control-lg" required/>
                          <label className="form-label text-left" for="typeUsernameX">User Name</label>
                        </div>
                        <div className="form-outline position-relative mb-3">
                          <input type="password" id="password" name="password" ref={passwordEle} className="form-control form-control-lg" required/>
                          <i className="fa fa-eye eye-icon" style={{color: "#152733"}} onClick={togglePasswordVisibility}></i>
                          <label className="form-label" for="password">Password</label>
                        </div>
                        <p className="small mb-3 pb-lg-2 text-center"><a className="text-white-50" href="{% url 'forgotPassword' %}">Forgot password?</a></p>
                        <div className="d-flex justify-content-center"><button className="btn btn-outline-light px-5" type="submit">Login</button></div>
                    </form>

                  </div>

                  <div>
                    <p className="mb-0 text-center">Don't have an account? <a className="text-white-50 fw-bold">Sign Up</a></p>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
  );
}

export default Login;
